import React, { Component } from 'react'

// PostCSS
import advancedVars from 'postcss-advanced-variables'
import autoprefixer from 'autoprefixer'
import calc from 'postcss-calc'
import colorAlpha from 'postcss-color-alpha'
import colorFunc from 'postcss-color-function'
import conditionals from 'postcss-conditionals'
import customMedia from 'postcss-custom-media'
import extend from 'postcss-extend'
import functions from 'postcss-functions'
import mediaMinMax from 'postcss-media-minmax'
import nested from 'postcss-nested'
import postcssImport from 'postcss-import'

const path = require('path')
//

// const SITE_ROOT = 'http://joshuar.com/'
const SITE_ROOT = '/'

export default {
  getSiteData: async () => ({
    title: 'JOSHUAR 🦁',
  }),

  devServer: {
    port: 3000,
    host: '127.0.0.1',
  },

  webpack: (config, { stage, defaultLoaders }) => {
    let loaders = [
      {
        loader: 'css-loader',
        options: {
          minimize: stage !== 'dev',
          importLoaders: 1,
        },
      },
      {
        loader: 'postcss-loader',
        options: {
          // Necessary for external CSS imports to work
          // https://github.com/facebookincubator/create-react-app/issues/2677
          ident: 'postcss',
          plugins: () => [
            postcssImport,
            autoprefixer({
              browsers: [
                '>1%',
                'last 4 versions',
                'Firefox ESR',
                'not ie < 9', // React doesn't support IE8 anyway
              ],
            }),
            colorAlpha,
            functions({
              glob: path.join(__dirname, 'src/styles/functions', '*.js'),
            }),
            advancedVars,
            customMedia,
            mediaMinMax,
            extend,
            nested,
            calc,
            colorFunc,
            conditionals,
          ],
        },
      },
    ]

    if (stage === 'dev') {
      loaders = ['style-loader'].concat(loaders)
    } else if (stage === 'prod') {
      loaders = ['style-loader'].concat(loaders)
    }

    config.module.rules = [
      {
        oneOf: [
          {
            test: /\.(js|jsx)$/,
            use: [
              {
                loader: 'babel-loader',
                options: {
                  cacheDirectory: stage !== 'prod',
                },
              },
            ],
          },
          {
            test: /\.css$/,
            use: loaders,
          },
          defaultLoaders.fileLoader,
        ],
      },
    ]
    return config
  },

  Document: class CustomDocument extends Component {
    render () {
      const {
        Html, Head, Body, children,
      } = this.props

      return (
        <Html>
          <Head>
            <meta charSet="UTF-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1" />
            <meta httpEquiv="Accept-CH" content="DPR, Viewport-Width, Width" />
          </Head>
          <Body>{children}</Body>
        </Html>
      )
    }
  },
}
